import {useOnDraw} from './Hooks';


const Canvas = ({
    width,
    height
}) => {

    const setCanvasRef = useOnDraw();

    function onDraw(ctx, point) {
        ctx.beginPath();
        ctx.arc(point.x, point.y, 2, 0, 2)
    }

    return (
        <canvas
            width={width}
            height={height}
            style={canvasStyle}
            ref={setCanvasRef}
        />
    )
}

export default Canvas;

const canvasStyle = {
    border: "1px solid black"
}
